mapper.select:
/* com.innobiz.orange.web.dm.dao.DmTakovrBDao.selectDmTakovrB */ SELECT
T.STOR_ID, T.DOC_GRP_ID, T.TAK_ORG_ID, T.TGT_ORG_ID, T.COMP_YN, T.TAK_STAT_CD, T.TAK_REGR_UID, T.TAK_REG_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.TAK_REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) TAK_REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_ORG_B WHERE ORG_ID = T.TAK_ORG_ID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS TAK_ORG_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_ORG_B WHERE ORG_ID = T.TGT_ORG_ID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS TGT_ORG_NM,
FROM DM_TAKOVR_B T
ORDER BY ${orderBy} ORDER BY T.TAK_REG_DT DESC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmTakovrBDao.countDmTakovrB */
SELECT COUNT(*) CNT
FROM DM_TAKOVR_B T

