mapper.select:
/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.selectDmTranProcL */
SELECT

	T.TRAN_ID,
	T.SEQ,
	T.TRAN_CONT,
	T.ALL_CNT,
	T.PROC_CNT,
	DATE_FORMAT(T.STRT_DT, '%Y-%m-%d %H:%i:%s') STRT_DT,
	DATE_FORMAT(T.END_DT, '%Y-%m-%d %H:%i:%s') END_DT,
	T.ERR_CONT,
	T.ERR_YN,

FROM DM_TRAN_PROC_L T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.TRAN_ID, T.SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.countDmTranProcL */
SELECT COUNT(*) CNT
FROM DM_TRAN_PROC_L T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.insertDmTranProcL */ INSERT INTO DM_TRAN_PROC_L
TRAN_ID, SEQ, TRAN_CONT, ALL_CNT, PROC_CNT, STRT_DT, END_DT, ERR_CONT, ERR_YN,
#{tranId, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SEQ),0)+1 FROM DM_TRAN_PROC_L P WHERE P.TRAN_ID = #{tranId, jdbcType=VARCHAR}), #{tranCont, jdbcType=VARCHAR}, NULL#{allCnt, jdbcType=BIGINT}, NULL#{procCnt, jdbcType=BIGINT}, NOW(),NULL, STR_TO_DATE(#{strtDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), NOW(),NULL, STR_TO_DATE(#{endDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{errCont, jdbcType=VARCHAR}, #{errYn, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.updateDmTranProcL */ UPDATE DM_TRAN_PROC_L
TRAN_ID = #{tranId, jdbcType=VARCHAR}, SEQ = #{seq, jdbcType=INTEGER}, TRAN_CONT = #{tranCont, jdbcType=VARCHAR}, ALL_CNT = NULL#{allCnt, jdbcType=BIGINT}, PROC_CNT = NULL#{procCnt, jdbcType=BIGINT},
STRT_DT = NOW(), STRT_DT = NULL, STRT_DT = STR_TO_DATE(#{strtDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
END_DT = NOW(), END_DT = NULL, END_DT = STR_TO_DATE(#{endDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
ERR_CONT = #{errCont, jdbcType=VARCHAR}, ERR_YN = #{errYn, jdbcType=VARCHAR},
AND TRAN_ID = #{tranId, jdbcType=VARCHAR} AND SEQ = #{seq, jdbcType=INTEGER} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.deleteDmTranProcL */
DELETE FROM DM_TRAN_PROC_L

	AND TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}
