mapper.select:
/* com.innobiz.orange.web.em.dao.EmTmpFileTDao.selectEmTmpFileT */
SELECT

	T.TMP_FILE_ID,
	T.DISP_NM,
	T.FILE_EXT,
	T.FILE_SIZE,
	T.SAVE_PATH,

FROM EM_TMP_FILE_T T

	 AND T.TMP_FILE_ID = #{tmpFileId, jdbcType=INTEGER}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.TMP_FILE_ID ASC

mapper.insert:
/* com.innobiz.orange.web.em.dao.EmTmpFileTDao.insertEmTmpFileT */ INSERT INTO EM_TMP_FILE_T
TMP_FILE_ID, DISP_NM, FILE_EXT, FILE_SIZE, SAVE_PATH,
#{tmpFileId, jdbcType=VARCHAR}, #{dispNm, jdbcType=VARCHAR}, #{fileExt, jdbcType=VARCHAR}, #{fileSize, jdbcType=INTEGER}, #{savePath, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.em.dao.EmTmpFileTDao.deleteEmTmpFileT */
DELETE FROM EM_TMP_FILE_T

	 AND TMP_FILE_ID = #{tmpFileId, jdbcType=INTEGER}
	 ${whereSqllet}
