mapper.select:
/* com.innobiz.orange.web.em.dao.EmUserSetupDDao.selectEmUserSetupD */
SELECT

	T.USER_UID,
	T.SETUP_CLS_ID,
	T.SETUP_ID,
	T.SETUP_VA,

FROM EM_USER_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.SETUP_CLS_ID, T.SETUP_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.em.dao.EmUserSetupDDao.countEmUserSetupD */
SELECT COUNT(*) CNT
FROM EM_USER_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	${whereSqllet}

