mapper.select:
/* com.innobiz.orange.web.or.dao.OrOdurBDao.countOrOdurB */
SELECT COUNT(*) CNT
FROM OR_ODUR_B T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.LGIN_ID = #{lginId, jdbcType=VARCHAR}
	AND T.RID = #{rid, jdbcType=VARCHAR}
	AND T.EIN = #{ein, jdbcType=VARCHAR}
	AND T.USER_STAT_CD = #{userStatCd, jdbcType=VARCHAR}
	AND T.REF_VA1 = #{refVa1, jdbcType=NVARCHAR}
	AND T.REF_VA2 = #{refVa2, jdbcType=NVARCHAR}
	AND T.REF_VA3 = #{refVa3, jdbcType=NVARCHAR}
	AND T.ODUR_UID IN (SELECT ODUR_UID FROM OR_USER_B WHERE USER_UID = #{userUid, jdbcType=VARCHAR})
	AND T.ODUR_UID IN (SELECT ODUR_UID FROM OR_USER_B WHERE ORG_ID = #{orgId, jdbcType=VARCHAR})
	AND T.ODUR_UID IN ( #{item, jdbcType=VARCHAR} )
	AND T.LGIN_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

