mapper.select:
/* com.innobiz.orange.web.or.dao.OrUserImgDDao.countOrUserImgD */
SELECT COUNT(*) CNT
FROM OR_USER_IMG_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.USER_IMG_TYP_CD = #{userImgTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.or.dao.OrUserImgDDao.insertOrUserImgD */ INSERT INTO OR_USER_IMG_D
USER_UID, USER_IMG_TYP_CD, IMG_PATH, IMG_WDTH, IMG_HGHT, MODR_UID, MOD_DT,
#{userUid, jdbcType=VARCHAR}, #{userImgTypCd, jdbcType=VARCHAR}, #{imgPath, jdbcType=VARCHAR}, NULL#{imgWdth, jdbcType=INTEGER}, NULL#{imgHght, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.or.dao.OrUserImgDDao.updateOrUserImgD */ UPDATE OR_USER_IMG_D
USER_UID = #{userUid, jdbcType=VARCHAR}, USER_IMG_TYP_CD = #{userImgTypCd, jdbcType=VARCHAR}, IMG_PATH = #{imgPath, jdbcType=VARCHAR}, IMG_WDTH = NULL#{imgWdth, jdbcType=INTEGER}, IMG_HGHT = NULL#{imgHght, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND USER_UID = #{userUid, jdbcType=VARCHAR} AND USER_IMG_TYP_CD = #{userImgTypCd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.or.dao.OrUserImgDDao.deleteOrUserImgD */
DELETE FROM OR_USER_IMG_D

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND USER_IMG_TYP_CD = #{userImgTypCd, jdbcType=VARCHAR}
	AND USER_UID IN (SELECT USER_UID FROM OR_USER_B WHERE COMP_ID = #{compId, jdbcType=VARCHAR} AND ODUR_UID != 'U0000001')
	${whereSqllet}
