mapper.select:
/* com.innobiz.orange.web.or.dao.OrUserPwHDao.countOrUserPwH */
SELECT COUNT(*) CNT
FROM OR_USER_PW_H T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.PW_TYP_CD = #{pwTypCd, jdbcType=VARCHAR}
	AND T.MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.or.dao.OrUserPwHDao.insertOrUserPwH */ INSERT INTO OR_USER_PW_H
ODUR_UID, PW_TYP_CD, MOD_DT, MODR_UID, PW_ENC,
#{odurUid, jdbcType=VARCHAR}, #{pwTypCd, jdbcType=VARCHAR}, STR_TO_DATE(DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s'),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, #{pwEnc, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.or.dao.OrUserPwHDao.updateOrUserPwH */
UPDATE OR_USER_PW_H

	ODUR_UID = #{odurUid, jdbcType=VARCHAR},
	PW_TYP_CD = #{pwTypCd, jdbcType=VARCHAR},
	
		MOD_DT = STR_TO_DATE(DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s'),
		MOD_DT = NULL,
		MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	
	MODR_UID = #{modrUid, jdbcType=VARCHAR},
	PW_ENC = #{pwEnc, jdbcType=VARCHAR},


	AND ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND PW_TYP_CD = #{pwTypCd, jdbcType=VARCHAR}
	AND MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.or.dao.OrUserPwHDao.deleteOrUserPwH */
DELETE FROM OR_USER_PW_H

	AND ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND PW_TYP_CD = #{pwTypCd, jdbcType=VARCHAR}
	AND MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
	${whereSqllet}
