	T.ODUR_UID,
	T.UAGNT_HASH_VA,
	T.UAGNT_TKN_VA,

FROM PT_USER_UAGNT_D T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.UAGNT_HASH_VA = #{uagntHashVa, jdbcType=INTEGER}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.ODUR_UID, T.UAGNT_HASH_VA

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtUserUagntDDao.countPtUserUagntD */
SELECT COUNT(*) CNT
FROM PT_USER_UAGNT_D T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.UAGNT_HASH_VA = #{uagntHashVa, jdbcType=INTEGER}
	${whereSqllet}

