mapper.select:
/* com.innobiz.orange.web.wa.dao.WaCarImgDDao.countWaCarImgD */
SELECT COUNT(*) CNT
FROM WA_CAR_IMG_D T

	AND T.CAR_KND_NO = #{carKndNo, jdbcType=INTEGER}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wa.dao.WaCarImgDDao.insertWaCarImgD */ INSERT INTO WA_CAR_IMG_D
CAR_KND_NO, IMG_PATH, IMG_WDTH, IMG_HGHT, MODR_UID, MOD_DT,
#{carKndNo, jdbcType=INTEGER}, #{imgPath, jdbcType=VARCHAR}, #{imgWdth, jdbcType=INTEGER}, #{imgHght, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.wa.dao.WaCarImgDDao.updateWaCarImgD */ UPDATE WA_CAR_IMG_D
IMG_PATH = #{imgPath, jdbcType=VARCHAR}, IMG_WDTH = #{imgWdth, jdbcType=INTEGER}, IMG_HGHT = #{imgHght, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND CAR_KND_NO = #{carKndNo, jdbcType=INTEGER} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wa.dao.WaCarImgDDao.deleteWaCarImgD */
DELETE FROM WA_CAR_IMG_D

	AND CAR_KND_NO = #{carKndNo, jdbcType=INTEGER}
	${whereSqllet}
