mapper.select:
/* com.innobiz.orange.web.wf.dao.WfFormLstDDao.selectWfFormLstD */ SELECT
T.GEN_ID, T.COLM_ID, T.FORM_NO, T.MD_CD, T.WDTH_PERC, T.ALN_VA, T.SORT_OPT_VA, T.DATA_SORT_VA, T.SORT_ORDR, T.SRCH_YN, D.COLM_NM, D.ITEM_NM, D.COLM_TYP_CD,
FROM WF_FORM_LST_D T LEFT OUTER JOIN WF_FORM_COLM_L D ON T.FORM_NO = D.FORM_NO AND T.COLM_ID = D.COLM_ID WF_FORM_LST_H T LEFT OUTER JOIN WF_FORM_COLM_H D ON T.GEN_ID = D.GEN_ID AND T.FORM_NO = D.FORM_NO AND T.COLM_ID = D.COLM_ID
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR ASC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

