mapper.select:
/* com.innobiz.orange.web.wf.dao.WfWorksReadHDao.selectWfWorksReadH */
SELECT

	T.WORK_NO,
	T.USER_UID,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	T.REG_DT,

FROM WFG_${formId}_WORKS_READ_H T

	 AND T.WORK_NO = #{workNo, jdbcType=INTEGER}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.REG_DT DESC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfWorksReadHDao.countWfWorksReadH */
SELECT COUNT(*) CNT
FROM WFG_${formId}_WORKS_READ_H T

	 AND T.WORK_NO = #{workNo, jdbcType=INTEGER}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wf.dao.WfWorksReadHDao.insertWfWorksReadH */ INSERT INTO WFG_${formId}_WORKS_READ_H
WORK_NO, USER_UID, REG_DT,
#{workNo, jdbcType=INTEGER}, #{userUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.wf.dao.WfWorksReadHDao.updateWfWorksReadH */
UPDATE WFG_${formId}_WORKS_READ_H

	 
		 REG_DT = NOW(),
		 REG_DT = NULL,
		 REG_DT = STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND WORK_NO = #{workNo, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wf.dao.WfWorksReadHDao.deleteWfWorksReadH */
DELETE FROM WFG_${formId}_WORKS_READ_H

	 AND WORK_NO = #{workNo, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
