mapper.select:
	/* com.innobiz.orange.web.wh.dao.WhReqStatDDao.selectWhReqStatD */
	SELECT
	
		T.REQ_NO,
        T.STAT_CD,
        T.HTML_YN,
	
	FROM WH_REQ_STAT_D T
	
	ORDER BY ${orderBy}
	ORDER BY T.REQ_NO ASC
	
	LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhReqStatDDao.countWhReqStatD */
SELECT COUNT(*) CNT
FROM WH_REQ_STAT_D T

mapper.insert:
/* com.innobiz.orange.web.wh.dao.WhReqStatDDao.insertWhReqStatD */ INSERT INTO WH_REQ_STAT_D
REQ_NO, STAT_CD, HTML_YN,
#{reqNo, jdbcType=INTEGER}, #{statCd, jdbcType=VARCHAR}, #{htmlYn, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wh.dao.WhReqStatDDao.updateWhReqStatD */
UPDATE WH_REQ_STAT_D

	 HTML_YN = #{htmlYn, jdbcType=INTEGER},


	 AND REQ_NO = #{reqNo, jdbcType=VARCHAR}
	 AND STAT_CD = #{statCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wh.dao.WhReqStatDDao.deleteWhReqStatD */
DELETE FROM WH_REQ_STAT_D

	 AND REQ_NO = #{reqNo, jdbcType=VARCHAR}
	 AND STAT_CD = #{statCd, jdbcType=VARCHAR}
