mapper.select:
/* com.innobiz.orange.web.wl.dao.WlLogFileDDao.selectWlLogFileD */ SELECT
T.FILE_ID, T.REF_ID, T.DISP_NM, T.DISP_ORDR, T.FILE_EXT, T.FILE_SIZE, T.SAVE_PATH, T.USE_YN, T.REGR_UID, T.REG_DT,
FROM WL_LOG_FILE_D T
AND T.REF_ID = #{refId, jdbcType=VARCHAR} AND T.FILE_ID = #{fileId, jdbcType=INTEGER} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.DISP_ORDR ASC

/* com.innobiz.orange.web.wl.dao.WlLogFileDDao.countWlLogFileD */
SELECT COUNT(*) CNT
FROM WL_LOG_FILE_D T

	 AND T.REF_ID = #{refId, jdbcType=VARCHAR}
	 AND T.FILE_ID = #{fileId, jdbcType=INTEGER}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wl.dao.WlLogFileDDao.insertWlLogFileD */ INSERT INTO WL_LOG_FILE_D
FILE_ID, REF_ID, DISP_NM, DISP_ORDR, FILE_EXT, FILE_SIZE, SAVE_PATH, USE_YN, REGR_UID, REG_DT,
#{fileId, jdbcType=VARCHAR}, #{refId, jdbcType=VARCHAR}, #{dispNm, jdbcType=VARCHAR}, #{dispOrdr, jdbcType=INTEGER}, #{fileExt, jdbcType=VARCHAR}, #{fileSize, jdbcType=INTEGER}, #{savePath, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.wl.dao.WlLogFileDDao.updateWlLogFileD */
UPDATE WL_LOG_FILE_D

	 REF_ID = #{newRefId, jdbcType=VARCHAR},
	 DISP_ORDR = #{dispOrdr, jdbcType=INTEGER},
	 USE_YN = #{useYn, jdbcType=VARCHAR},


	 AND FILE_ID = #{fileId, jdbcType=INTEGER}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wl.dao.WlLogFileDDao.deleteWlLogFileD */
DELETE FROM WL_LOG_FILE_D

	 AND FILE_ID = #{fileId, jdbcType=INTEGER}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
