mapper.select:
/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.selectWrRescKndB */ SELECT
T.RESC_KND_ID, T.COMP_ID, T.RESC_ID, T.BGCOL_CD, T.USE_YN, T.SORT_ORDR, T.REGR_UID, DATE_FORMAT(T.REG_DT, '%Y-%m-%d %H:%i:%s') REG_DT, (SELECT RESC_VA FROM WR_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS KND_NM , (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM,
FROM WR_RESC_KND_B T INNER JOIN WR_RESC_B R ON R.RESC_ID = T.RESC_ID AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.countWrRescKndB */
SELECT COUNT(*) CNT
FROM WR_RESC_KND_B T INNER JOIN WR_RESC_B R ON R.RESC_ID = T.RESC_ID AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')

mapper.insert:
/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.insertWrRescKndB */ INSERT INTO WR_RESC_KND_B
RESC_KND_ID, COMP_ID, RESC_ID, KND_NM, BGCOL_CD, USE_YN, SORT_ORDR, REGR_UID, REG_DT,
#{rescKndId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{kndNm, jdbcType=VARCHAR}, #{bgcolCd, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL,

mapper.update:
/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.updateWrRescKndB */
UPDATE WR_RESC_KND_B

	 KND_NM = #{kndNm, jdbcType=VARCHAR},
	 BGCOL_CD = #{bgcolCd, jdbcType=VARCHAR},
	 USE_YN = #{useYn, jdbcType=VARCHAR},
	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND RESC_KND_ID = #{rescKndId, jdbcType=VARCHAR}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.deleteWrRescKndB */
DELETE FROM WR_RESC_KND_B

	 AND RESC_KND_ID = #{rescKndId, jdbcType=VARCHAR}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.select:
/* com.innobiz.orange.web.wr.dao.WrRescKndBDao.maxWrRescKndB */
SELECT MAX(IFNULL(SORT_ORDR,0))+1 AS CNT
FROM WR_RESC_KND_B T

 AND COMP_ID = #{compId, jdbcType=VARCHAR}
