mapper.select:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.countApAgnApntD */
SELECT COUNT(*) CNT
FROM AP_AGN_APNT_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.END_DT >= TO_DATE(#{durEndDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	AND T.STRT_DT <= TO_DATE(#{durStrtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.insertApAgnApntD */ INSERT INTO AP_AGN_APNT_D
USER_UID, AGNT_UID, SEQ, END_DT, STRT_DT, AGNT_RESC_ID, ABS_RSON_CD, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{userUid, jdbcType=VARCHAR}, #{agntUid, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT NVL(MAX(SEQ),0)+1 FROM AP_AGN_APNT_D WHERE USER_UID = #{userUid, jdbcType=VARCHAR} AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}), SYSDATE,NULL, TO_DATE(#{endDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), SYSDATE,NULL, TO_DATE(#{strtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{agntRescId, jdbcType=VARCHAR}, #{absRsonCd, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApAgnApntDDao.deleteApAgnApntD */
DELETE FROM AP_AGN_APNT_D

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	AND SEQ = #{seq, jdbcType=INTEGER}
	AND END_DT = TO_DATE(#{endDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	AND T.END_DT >= TO_DATE(#{durEndDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	AND T.STRT_DT <= TO_DATE(#{durStrtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS')
	${whereSqllet}
