mapper.select:
/* com.innobiz.orange.web.ap.dao.ApClsInfoDDao.countApClsInfoD */
SELECT COUNT(*) CNT
FROM AP_CLS_INFO_D T

	AND T.ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND T.CLS_INFO_ID = #{clsInfoId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApClsInfoDDao.insertApClsInfoD */ INSERT INTO AP_CLS_INFO_D
ORG_ID, CLS_INFO_ID, CLS_INFO_PID, RESC_ID, CLS_INFO_NM, SORT_ORDR, USE_YN, SYS_CLS_INFO_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{orgId, jdbcType=VARCHAR}, #{clsInfoId, jdbcType=VARCHAR}, #{clsInfoPid, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{clsInfoNm, jdbcType=NVARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM AP_CLS_INFO_D WHERE ORG_ID = #{orgId, jdbcType=VARCHAR} AND CLS_INFO_PID = #{clsInfoPid, jdbcType=VARCHAR}), #{useYn, jdbcType=VARCHAR}, #{sysClsInfoYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApClsInfoDDao.updateApClsInfoD */ UPDATE AP_CLS_INFO_D
ORG_ID = #{orgId, jdbcType=VARCHAR}, CLS_INFO_ID = #{clsInfoId, jdbcType=VARCHAR}, CLS_INFO_PID = #{clsInfoPid, jdbcType=VARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, CLS_INFO_NM = #{clsInfoNm, jdbcType=NVARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=VARCHAR}, SYS_CLS_INFO_YN = #{sysClsInfoYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = SYSDATE, REG_DT = NULL, REG_DT = TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
AND ORG_ID = #{orgId, jdbcType=VARCHAR} AND CLS_INFO_ID = #{clsInfoId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApClsInfoDDao.deleteApClsInfoD */
DELETE FROM AP_CLS_INFO_D

	AND ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND CLS_INFO_ID = #{clsInfoId, jdbcType=VARCHAR}
	${whereSqllet}
