mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormTxtDDao.countApOngoFormTxtD */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_TXT_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_TXT_TYP_CD = #{formTxtTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngoFormTxtDDao.insertApOngoFormTxtD */ INSERT INTO AP_ONGO_FORM_TXT_D
FORM_ID, FORM_SEQ, FORM_TXT_TYP_CD, TXT_CONT, TXT_FONT_VA, TXT_STYL_VA, TXT_SIZE, TXT_COLR_VA,
#{formId, jdbcType=VARCHAR}, #{formSeq, jdbcType=INTEGER}, #{formTxtTypCd, jdbcType=VARCHAR}, #{txtCont, jdbcType=NVARCHAR}, #{txtFontVa, jdbcType=NVARCHAR}, #{txtStylVa, jdbcType=NVARCHAR}, #{txtSize, jdbcType=VARCHAR}, #{txtColrVa, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngoFormTxtDDao.updateApOngoFormTxtD */
UPDATE AP_ONGO_FORM_TXT_D

	FORM_ID = #{formId, jdbcType=VARCHAR},
	FORM_SEQ = #{formSeq, jdbcType=INTEGER},
	FORM_TXT_TYP_CD = #{formTxtTypCd, jdbcType=VARCHAR},
	TXT_CONT = #{txtCont, jdbcType=NVARCHAR},
	TXT_FONT_VA = #{txtFontVa, jdbcType=NVARCHAR},
	TXT_STYL_VA = #{txtStylVa, jdbcType=NVARCHAR},
	TXT_SIZE = #{txtSize, jdbcType=VARCHAR},
	TXT_COLR_VA = #{txtColrVa, jdbcType=VARCHAR},


	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND FORM_TXT_TYP_CD = #{formTxtTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngoFormTxtDDao.deleteApOngoFormTxtD */
DELETE FROM AP_ONGO_FORM_TXT_D

	AND FORM_ID = #{formId, jdbcType=VARCHAR}
	AND FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND FORM_TXT_TYP_CD = #{formTxtTypCd, jdbcType=VARCHAR}
	${whereSqllet}
