FROM AP_RECV_GRP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.RECV_GRP_ID = #{recvGrpId, jdbcType=VARCHAR}
	AND T.RECV_GRP_SEQ = #{recvGrpSeq, jdbcType=INTEGER}
	${whereSqllet}

