mapper.select:
/* com.innobiz.orange.web.ap.dao.ApRescDDao.selectApRescD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.COMP_ID,
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA,

FROM AP_RESC_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.COMP_ID, T.RESC_ID, T.LANG_TYP_CD

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApRescDDao.countApRescD */
SELECT COUNT(*) CNT
FROM AP_RESC_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApRescDDao.insertApRescD */ INSERT INTO AP_RESC_D
COMP_ID, RESC_ID, LANG_TYP_CD, RESC_VA,
#{compId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApRescDDao.updateApRescD */
UPDATE AP_RESC_D

	COMP_ID = #{compId, jdbcType=VARCHAR},
	RESC_ID = #{rescId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	RESC_VA = #{rescVa, jdbcType=NVARCHAR},


	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApRescDDao.deleteApRescD */
DELETE FROM AP_RESC_D

	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}
