mapper.select:
/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.selectBaBullPopupD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	BULL_ID,
	BRD_ID,
	START_DT,
	END_DT,
	WIDTH,
	DISP_YN,
	USE_YN,
	COMP_ID,
	MODR_UID,
	TO_CHAR(MOD_DT, 'YYYY-MM-DD HH24:MI:SS') MOD_DT,

FROM BA_BULL_POPUP_D

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND TO_CHAR(START_DT, 'YYYY-MM-DD') >= #{todayYmd, jdbcType=VARCHAR}
			 AND TO_CHAR(END_DT, 'YYYY-MM-DD') <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

ORDER BY ${orderBy}
ORDER BY BRD_ID DESC

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.countBaBullPopupD */
SELECT COUNT(*) CNT
FROM BA_BULL_POPUP_D 

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND TO_CHAR(START_DT, 'YYYY-MM-DD') >= #{todayYmd, jdbcType=VARCHAR}
			 AND TO_CHAR(END_DT, 'YYYY-MM-DD') <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

