BULL_ID, BRD_ID, START_DT, END_DT, WIDTH, DISP_YN, USE_YN, COMP_ID, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, TO_DATE(#{strtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), TO_DATE(#{endDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{width, jdbcType=INTEGER}, #{dispYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
