START_DT = TO_DATE(#{strtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), END_DT = TO_DATE(#{endDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), WIDTH = #{width, jdbcType=INTEGER}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
