mapper.select:
/* com.innobiz.orange.web.bb.dao.BaCatDDao.selectBaCatD */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
T.CAT_ID, T.CAT_GRP_ID, T.CAT_NM, T.RESC_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.DISP_ORDR, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM BA_CAT_D T
AND T.CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR} AND T.CAT_ID = #{catId, jdbcType=VARCHAR} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.DISP_ORDR ASC
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.bb.dao.BaCatDDao.countBaCatD */
SELECT COUNT(*) CNT
FROM BA_CAT_D T, BA_RESC_B R

	 AND T.CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND T.CAT_ID = #{catId, jdbcType=VARCHAR}
	 ${whereSqllet}

