/* com.innobiz.orange.web.bb.dao.BaCatDDao.insertBaCatD */ INSERT INTO BA_CAT_D
CAT_ID, CAT_GRP_ID, CAT_NM, RESC_ID, DISP_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{catId, jdbcType=VARCHAR}, #{catGrpId, jdbcType=VARCHAR}, #{catNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{dispOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.BaCatDDao.updateBaCatD */
UPDATE BA_CAT_D

	 CAT_NM = #{catNm, jdbcType=NVARCHAR},
	 DISP_ORDR = #{dispOrdr, jdbcType=INTEGER},
