mapper.select:
/* com.innobiz.orange.web.ct.dao.CtAdmNotcBDao.selectCtAdmNotcB */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
CANB_T.BULL_ID, CANB_T.COMP_ID, CANB_T.BULL_PID, CANB_T.REPLY_GRP_ID, CANB_T.REPLY_ORDR, CANB_T.REPLY_DPTH, CANB_T.BULL_STAT_CD, CANB_T.BULL_REZV_YN, CANB_T.TGT_DEPT_YN, CANB_T.TGT_USER_YN, CANB_T.SUBJ, CANB_T.BULL_REZV_DT, CANB_T.BULL_EXPR_DT, CANB_T.CONT, CANB_T.READ_CNT, CANB_T.PROS_CNT, CANB_T.CONS_CNT, CANB_T.RECMD_CNT, CANB_T.REGR_UID, CANB_T.REG_DT, CANB_T.MODR_UID, CANB_T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CANB_T.REGR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CANB_T.MODR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, ( SELECT CASE WHEN COUNT(1) > 0 THEN 'Y' ELSE 'N' END ATT_YN FROM CT_FILE_D WHERE REF_ID=TO_CHAR(CANB_T.BULL_ID)
) AS ATT_YN, CASE WHEN CANB_T.REG_DT > SYSDATE -1 THEN 'Y' ELSE 'N' END NEW_YN, CASE WHEN CANB_T.BULL_EXPR_DT < SYSDATE -1 THEN 'Y' ELSE 'N' END EXPR_YN,
FROM CT_ADM_NOTC_B CANB_T
LEFT JOIN OR_USER_B U ON CANB_T.REGR_UID = U.USER_UID LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY CANB_T.BULL_ID
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtAdmNotcBDao.insertCtAdmNotcB */ INSERT INTO CT_ADM_NOTC_B
BULL_ID, COMP_ID, BULL_PID, REPLY_GRP_ID, REPLY_ORDR, REPLY_DPTH, BULL_STAT_CD, BULL_REZV_YN, TGT_DEPT_YN, TGT_USER_YN, SUBJ, BULL_REZV_DT, BULL_EXPR_DT, CONT, READ_CNT, PROS_CNT, CONS_CNT, RECMD_CNT, REGR_UID, REG_DT, MODR_UID, MOD_DT, ATT_YN,
#{bullId, jdbcType=NUMERIC}, #{compId, jdbcType=VARCHAR}, #{bullPid, jdbcType=NUMERIC}, #{replyGrpId, jdbcType=NUMERIC}, #{replyOrdr, jdbcType=NUMERIC}, #{replyDpth, jdbcType=NUMERIC}, #{bullStatCd, jdbcType=VARCHAR}, #{bullRezvYn, jdbcType=CHAR}, #{tgtDeptYn, jdbcType=CHAR}, #{tgtUserYn, jdbcType=CHAR}, #{subj, jdbcType=NVARCHAR}, NULL, TO_DATE(#{bullRezvDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), NULL, TO_DATE(#{bullExprDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{cont, jdbcType=NCLOB}, #{readCnt, jdbcType=NUMERIC}, #{prosCnt, jdbcType=NUMERIC}, #{consCnt, jdbcType=NUMERIC}, #{recmdCnt, jdbcType=NUMERIC}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{attYn, jdbcType=CHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtAdmNotcBDao.updateCtAdmNotcB */ UPDATE CT_ADM_NOTC_B
BULL_PID = #{bullPid, jdbcType=NUMERIC}, REPLY_GRP_ID = #{replyGrpId, jdbcType=NUMERIC}, REPLY_ORDR = #{replyOrdr, jdbcType=NUMERIC}, REPLY_DPTH = #{replyDpth, jdbcType=NUMERIC}, BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR}, BULL_REZV_YN = #{bullRezvYn, jdbcType=CHAR}, TGT_DEPT_YN = #{tgtDeptYn, jdbcType=CHAR}, TGT_USER_YN = #{tgtUserYn, jdbcType=CHAR}, SUBJ = #{subj, jdbcType=NVARCHAR}, BULL_REZV_DT = TO_DATE(#{bullRezvDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), BULL_EXPR_DT = TO_DATE(#{bullExprDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), CONT = #{cont, jdbcType=NVARCHAR}, READ_CNT = #{readCnt, jdbcType=NUMERIC}, PROS_CNT = #{prosCnt, jdbcType=NUMERIC}, CONS_CNT = #{consCnt, jdbcType=NUMERIC}, RECMD_CNT = #{recmdCnt, jdbcType=NUMERIC}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
ATT_YN = #{attYn, jdbcType=CHAR},
AND BULL_ID = #{bullId, jdbcType=NUMERIC} AND COMP_ID = #{compId, jdbcType=VARCHAR}

/* com.innobiz.orange.web.ct.dao.CtAdmNotcBDao.updateReadCnt */
UPDATE CT_ADM_NOTC_B

	READ_CNT = READ_CNT + 1,


	AND BULL_ID = #{bullId, jdbcType=NUMERIC}

mapper.delete:
	/* com.innobiz.orange.web.ct.dao.CtAdmNotcBDao.deleteCtAdmNotcB */
	DELETE FROM CT_ADM_NOTC_B
	
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}
	     AND COMP_ID = #{compId, jdbcType=VARCHAR}
