mapper.select:
/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.selectDmTranProcL */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.TRAN_ID,
	T.SEQ,
	T.TRAN_CONT,
	T.ALL_CNT,
	T.PROC_CNT,
	TO_CHAR(T.STRT_DT, 'YYYY-MM-DD HH24:MI:SS') STRT_DT,
	TO_CHAR(T.END_DT, 'YYYY-MM-DD HH24:MI:SS') END_DT,
	T.ERR_CONT,
	T.ERR_YN,

FROM DM_TRAN_PROC_L T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.TRAN_ID, T.SEQ


) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.dm.dao.DmTranProcLDao.countDmTranProcL */
SELECT COUNT(*) CNT
FROM DM_TRAN_PROC_L T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.SEQ >= #{strtSeq, jdbcType=NUMERIC}
	${whereSqllet}

