#{tranId, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT NVL(MAX(SEQ),0)+1 FROM AP_TRAN_PROC_L WHERE TRAN_ID = #{tranId, jdbcType=VARCHAR}), #{tranCont, jdbcType=NVARCHAR}, NULL#{allCnt, jdbcType=BIGINT}, NULL#{procCnt, jdbcType=BIGINT}, SYSDATE,NULL, TO_DATE(#{strtDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), SYSDATE,NULL, TO_DATE(#{endDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{errCont, jdbcType=NVARCHAR}, #{errYn, jdbcType=VARCHAR},

