	T.EMAIL_ID,
	T.DISP_NM,
	T.DISP_ORDR,
	T.FILE_EXT,
	T.FILE_SIZE,
	T.FILE_CONT,

FROM CM_EMAIL_FILE_D T

	 AND T.EMAIL_ID = #{emailId, jdbcType=INTEGER}
	 AND T.DISP_ORDR = #{dispOrdr, jdbcType=INTEGER}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.DISP_ORDR ASC

