mapper.select:
/* com.innobiz.orange.web.or.dao.OrDbIntgDDao.selectOrDbIntgD */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
T.COMP_ID, T.DB_USER_ID, T.DB_PW, T.DB_DRIVER, T.DB_URL, T.DB_TEST_SQL, T.CD_DEL_CD, T.ORG_DEL_CD, T.USER_DEL_CD, T.DEL_ORG_MOVE_ORG_ID, T.DEL_USER_MOVE_ORG_ID, T.NO_DEPT_ORG_ID, T.INTG_TM, T.USE_YN, T.ORG_SQL, T.CD_SQL, T.USER_SQL, T.ADU_SQL,
FROM OR_DB_INTG_D T
AND T.COMP_ID = #{compId, jdbcType=VARCHAR} AND T.USE_YN = #{useYn, jdbcType=VARCHAR} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.COMP_ID
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.or.dao.OrDbIntgDDao.countOrDbIntgD */
SELECT COUNT(*) CNT
FROM OR_DB_INTG_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

