mapper.select:
/* com.innobiz.orange.web.or.dao.OrOrgCntcDDao.selectOrOrgCntcD */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
T.ORG_ID, T.REP_EMAIL, T.PHON, T.FNO, T.ZIP_NO, T.ADR, T.DETL_ADR, T.REP_HPAGE_URL,
FROM OR_ORG_CNTC_D T
AND T.ORG_ID = #{orgId, jdbcType=VARCHAR} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.ORG_ID
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.or.dao.OrOrgCntcDDao.countOrOrgCntcD */
SELECT COUNT(*) CNT
FROM OR_ORG_CNTC_D T

	AND T.ORG_ID = #{orgId, jdbcType=VARCHAR}
	${whereSqllet}

