mapper.select:
/* com.innobiz.orange.web.or.dao.OrRescBDao.selectOrRescB */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA,

FROM OR_RESC_B T

	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.RESC_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.RESC_ID, T.LANG_TYP_CD

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.or.dao.OrRescBDao.countOrRescB */
SELECT COUNT(*) CNT
FROM OR_RESC_B T

	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.RESC_ID IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

