mapper.select:
/* com.innobiz.orange.web.pt.dao.PtLstSetupDDao.countPtLstSetupD */
SELECT COUNT(*) CNT
FROM PT_LST_SETUP_D T

	AND T.LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}
	AND T.DFT_VA_YN = #{dftVaYn, jdbcType=VARCHAR}
	AND T.ATRB_ID = #{atrbId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtLstSetupDDao.insertPtLstSetupD */ INSERT INTO PT_LST_SETUP_D
LST_SETUP_META_ID, DFT_VA_YN, ATRB_ID, MSG_ID, WDTH_PERC, ALN_VA, SORT_OPT_VA, DATA_SORT_VA, DISP_YN, SORT_ORDR, MODR_UID, MOD_DT,
#{lstSetupMetaId, jdbcType=VARCHAR}, #{dftVaYn, jdbcType=VARCHAR}, #{atrbId, jdbcType=VARCHAR}, #{msgId, jdbcType=VARCHAR}, #{wdthPerc, jdbcType=VARCHAR}, #{alnVa, jdbcType=VARCHAR}, #{sortOptVa, jdbcType=VARCHAR}, #{dataSortVa, jdbcType=VARCHAR}, #{dispYn, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM PT_LST_SETUP_D WHERE LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR} AND DFT_VA_YN = #{dftVaYn, jdbcType=VARCHAR} AND ATRB_ID = #{atrbId, jdbcType=VARCHAR}), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtLstSetupDDao.updatePtLstSetupD */ UPDATE PT_LST_SETUP_D
LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR}, DFT_VA_YN = #{dftVaYn, jdbcType=VARCHAR}, ATRB_ID = #{atrbId, jdbcType=VARCHAR}, MSG_ID = #{msgId, jdbcType=VARCHAR}, WDTH_PERC = #{wdthPerc, jdbcType=VARCHAR}, ALN_VA = #{alnVa, jdbcType=VARCHAR}, SORT_OPT_VA = #{sortOptVa, jdbcType=VARCHAR}, DATA_SORT_VA = #{dataSortVa, jdbcType=VARCHAR}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
AND LST_SETUP_META_ID = #{lstSetupMetaId, jdbcType=VARCHAR} AND DFT_VA_YN = #{dftVaYn, jdbcType=VARCHAR} AND ATRB_ID = #{atrbId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtLstSetupDDao.deletePtLstSetupD */
