mapper.select:
/* com.innobiz.orange.web.pt.dao.PtMnuLoutCombDDao.countPtMnuLoutCombD */
SELECT COUNT(*) CNT
FROM PT_MNU_LOUT_COMB_D T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}
	AND T.MNU_LOUT_ID = #{mnuLoutId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtMnuLoutCombDDao.insertPtMnuLoutCombD */ INSERT INTO PT_MNU_LOUT_COMB_D
COMP_ID, MNU_LOUT_COMB_ID, MNU_LOUT_ID, LOUT_CAT_ID, MNU_LOUT_COMB_PID, FLD_YN, FLD_NM, RESC_ID, MNU_ID, MNU_RESC_ID, SORT_ORDR,
#{compId, jdbcType=VARCHAR}, #{mnuLoutCombId, jdbcType=VARCHAR}, #{mnuLoutId, jdbcType=VARCHAR}, #{loutCatId, jdbcType=VARCHAR}, #{mnuLoutCombPid, jdbcType=VARCHAR}, #{fldYn, jdbcType=VARCHAR}, #{fldNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{mnuId, jdbcType=VARCHAR}, #{mnuRescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM PT_MNU_LOUT_COMB_D WHERE COMP_ID = #{compId, jdbcType=VARCHAR} AND MNU_LOUT_ID = #{mnuLoutId, jdbcType=VARCHAR} AND MNU_LOUT_COMB_PID = #{mnuLoutCombPid, jdbcType=VARCHAR}),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtMnuLoutCombDDao.updatePtMnuLoutCombD */
UPDATE PT_MNU_LOUT_COMB_D

	COMP_ID = #{compId, jdbcType=VARCHAR},
	MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR},
	LOUT_CAT_ID = #{loutCatId, jdbcType=VARCHAR},
	MNU_LOUT_COMB_PID = #{mnuLoutCombPid, jdbcType=VARCHAR},
	FLD_YN = #{fldYn, jdbcType=VARCHAR},
	FLD_NM = #{fldNm, jdbcType=NVARCHAR},
	RESC_ID = #{rescId, jdbcType=VARCHAR},
	MNU_ID = #{mnuId, jdbcType=VARCHAR},
	MNU_RESC_ID = #{mnuRescId, jdbcType=VARCHAR},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}
	AND MNU_LOUT_ID = #{mnuLoutId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtMnuLoutCombDDao.deletePtMnuLoutCombD */
DELETE FROM PT_MNU_LOUT_COMB_D

	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	AND MNU_LOUT_COMB_ID = #{mnuLoutCombId, jdbcType=VARCHAR}
	AND MNU_LOUT_ID = #{mnuLoutId, jdbcType=VARCHAR}
	${whereSqllet}
