	T.SETUP_VA,
	T.SORT_ORDR,

FROM PT_SYS_SETUP_D T

	AND T.SETUP_CLS_ID = #{setupClsId, jdbcType=VARCHAR}
	AND T.SETUP_ID = #{setupId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.SETUP_CLS_ID, T.SETUP_ID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

