mapper.select:
/* com.innobiz.orange.web.wf.dao.WfWorksImgDDao.selectWfWorksImgD */

SELECT

	M.*,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = M.MODR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) MODR_NM,

FROM ( SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.IMG_NO,
	T.WORK_NO,
	T.COLM_NM,
	T.IMG_PATH,
	T.IMG_WDTH,
	T.IMG_HGHT,
	T.MODR_UID,
	T.MOD_DT,

FROM WFG_${formId}_WORKS_IMG_D T

	 AND T.IMG_NO = #{imgNo, jdbcType=VARCHAR}
	 AND T.WORK_NO = #{workNo, jdbcType=VARCHAR}
	 AND T.COLM_NM = #{colmNm, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.WORK_NO

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M ) M

/* com.innobiz.orange.web.wf.dao.WfWorksImgDDao.countWfWorksImgD */
SELECT COUNT(*) CNT
FROM WFG_${formId}_WORKS_IMG_D T

	 AND T.IMG_NO = #{imgNo, jdbcType=VARCHAR}
	 AND T.WORK_NO = #{workNo, jdbcType=VARCHAR}
	 AND T.COLM_NM = #{colmNm, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.wf.dao.WfWorksImgDDao.insertWfWorksImgD */ INSERT INTO WFG_${formId}_WORKS_IMG_D
IMG_NO, WORK_NO, COLM_NM, IMG_PATH, IMG_WDTH, IMG_HGHT, MODR_UID, MOD_DT,
#{imgNo, jdbcType=VARCHAR}, #{workNo, jdbcType=VARCHAR}, #{colmNm, jdbcType=VARCHAR}, #{imgPath, jdbcType=VARCHAR}, #{imgWdth, jdbcType=INTEGER}, #{imgHght, jdbcType=INTEGER}, #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.wf.dao.WfWorksImgDDao.updateWfWorksImgD */
UPDATE WFG_${formId}_WORKS_IMG_D

	 COLM_NM = #{colmNm, jdbcType=VARCHAR}
	 IMG_PATH = #{imgPath, jdbcType=VARCHAR},
	 IMG_WDTH = #{imgWdth, jdbcType=INTEGER},
	 IMG_HGHT = #{imgHght, jdbcType=INTEGER},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		 MOD_DT = SYSDATE,
		 MOD_DT = NULL,
		 MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS'),
	


	AND IMG_NO = #{imgNo, jdbcType=VARCHAR}
	AND WORK_NO = #{workNo, jdbcType=VARCHAR}			
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.wf.dao.WfWorksImgDDao.deleteWfWorksImgD */
DELETE FROM WFG_${formId}_WORKS_IMG_D

	AND IMG_NO = #{imgNo, jdbcType=VARCHAR}
	AND WORK_NO = #{workNo, jdbcType=VARCHAR}
	AND COLM_NM = #{colmNm, jdbcType=VARCHAR}
	${whereSqllet}
