mapper.select:
/* com.innobiz.orange.web.wv.dao.WvQuesExamDDao.countWvQuesExamD */
SELECT COUNT(*) CNT
FROM WV_QUES_EXAM_D WQED_T

mapper.insert:
/* com.innobiz.orange.web.wv.dao.WvQuesExamDDao.insertWvQuesExamD */ INSERT INTO WV_QUES_EXAM_D
QUES_ID, COMP_ID, SURV_ID, EXAM_NO, EXAM_ORDR, EXAM_DISP_NM, INPUT_YN, EXAM_IMG_USE_YN, IMG_SURV_FILE_ID,
#{quesId, jdbcType=VARCHAR},#{compId, jdbcType=CHAR},#{survId, jdbcType=VARCHAR}, #{examNo, jdbcType=NUMERIC},#{examOrdr, jdbcType=NUMERIC},#{examDispNm, jdbcType=NVARCHAR}, #{inputYn, jdbcType=CHAR},#{examImgUseYn, jdbcType=CHAR},#{imgSurvFileId, jdbcType=VARCHAR},

mapper.update:
		/* com.innobiz.orange.web.wv.dao.WvQuesExamDDao.updateWvQuesExamD */
		UPDATE WV_QUES_EXAM_D
		
			 RESC_VA = #{rescVa, jdbcType=VARCHAR},
		
		
		     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
		     AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND SURV_ID = #{survId, jdbcType=VARCHAR}
   		     AND EXAM_NO = #{examNo, jdbcType=NUMERIC}

mapper.delete:
		/* com.innobiz.orange.web.wv.dao.WvQuesExamDDao.deleteWvQuesExamD */
		DELETE FROM WV_QUES_EXAM_D
		
		     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
		     AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND SURV_ID = #{survId, jdbcType=VARCHAR}
   		     AND EXAM_NO = #{examNo, jdbcType=NUMERIC}
