mapper.select:
/* com.innobiz.orange.web.wv.dao.WvSurvPolcDDao.selectWvSurvPolcD */


SELECT * FROM (

SELECT


	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	WSPD_T.COMP_ID, WSPD_T.APVD_YN, WSPD_T.REG_DT,
	WSPD_T.MOD_DT, WSPD_T.REGR_UID, WSPD_T.MODR_UID, 
	
	

FROM WV_SURV_POLC_D WSPD_T


ORDER BY ${orderBy}
ORDER BY WSPD_T.COMP_ID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.wv.dao.WvSurvPolcDDao.countWvSurvPolcD */
SELECT COUNT(*) CNT
FROM WV_SURV_POLC_D WSPD_T

