mapper.select:
/* com.innobiz.orange.web.ap.dao.ApErpIntgChitDDao.selectApErpIntgChitD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.INTG_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.INTG_NO,
	T.CHIT_FORM_ID,
	T.FORM_NM,
	T.CHIT_BODY_HTML,

FROM AP_ERP_INTG_CHIT_D T

	AND T.INTG_NO = #{intgNo, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.INTG_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApErpIntgChitDDao.countApErpIntgChitD */
SELECT COUNT(*) CNT
FROM AP_ERP_INTG_CHIT_D T

	AND T.INTG_NO = #{intgNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApErpIntgChitDDao.insertApErpIntgChitD */ INSERT INTO AP_ERP_INTG_CHIT_D
INTG_NO, CHIT_FORM_ID, FORM_NM, CHIT_BODY_HTML,
#{intgNo, jdbcType=BIGINT}, #{chitFormId, jdbcType=VARCHAR}, #{formNm, jdbcType=NVARCHAR}, #{chitBodyHtml, jdbcType=CLOB},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApErpIntgChitDDao.updateApErpIntgChitD */
UPDATE AP_ERP_INTG_CHIT_D

	INTG_NO = #{intgNo, jdbcType=BIGINT},
	CHIT_FORM_ID = #{chitFormId, jdbcType=VARCHAR},
	FORM_NM = #{formNm, jdbcType=NVARCHAR},
	CHIT_BODY_HTML = #{chitBodyHtml, jdbcType=CLOB},


	AND INTG_NO = #{intgNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApErpIntgChitDDao.deleteApErpIntgChitD */
DELETE FROM AP_ERP_INTG_CHIT_D

	AND INTG_NO = #{intgNo, jdbcType=BIGINT}
	${whereSqllet}
