mapper.select:
/* com.innobiz.orange.web.ap.dao.ApFormJspDDao.selectApFormJspD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.JSP_ID DESC, N.FORM_ID DESC) AS RNUM, N.* FROM (SELECT
	
	T.JSP_ID,
	T.FORM_ID,
	T.JSP_PATH,

FROM AP_FORM_JSP_D T

	AND T.JSP_ID = #{jspId, jdbcType=VARCHAR}
	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.JSP_ID, T.FORM_ID


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApFormJspDDao.countApFormJspD */
SELECT COUNT(*) CNT
FROM AP_FORM_JSP_D T

	AND T.JSP_ID = #{jspId, jdbcType=VARCHAR}
	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	${whereSqllet}

