mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.selectApOngdCnclD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.MAKR_UID DESC, N.XML_TYP_ID DESC, N.APV_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.MAKR_UID,
	T.XML_TYP_ID,
	T.APV_NO,

FROM AP_ONGD_CNCL_D T

	AND T.MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND T.XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.MAKR_UID, T.XML_TYP_ID, T.APV_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdCnclDDao.countApOngdCnclD */
SELECT COUNT(*) CNT
FROM AP_ONGD_CNCL_D T

	AND T.MAKR_UID = #{makrUid, jdbcType=VARCHAR}
	AND T.XML_TYP_ID = #{xmlTypId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

