mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.selectApOngdPubBxD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.PUB_BX_DEPT_ID DESC, N.APV_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.PUB_BX_DEPT_ID,
	T.APV_NO,
	CONVERT(VARCHAR(19), T.PUB_BX_DT, 120) PUB_BX_DT,
	CONVERT(VARCHAR(10), T.PUB_BX_END_YMD, 120) PUB_BX_END_YMD,
	T.REGR_UID,
	T.REGR_NM,
	T.REG_DEPT_ID,
	T.REG_DEPT_NM,

FROM AP_${storage}_PUB_BX_D T

	AND T.PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.PUB_BX_DEPT_ID, T.APV_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.countApOngdPubBxD */
SELECT COUNT(*) CNT
FROM AP_${storage}_PUB_BX_D T

	AND T.PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.insertApOngdPubBxD */ INSERT INTO AP_${storage}_PUB_BX_D
PUB_BX_DEPT_ID, APV_NO, PUB_BX_DT, PUB_BX_END_YMD, REGR_UID, REGR_NM, REG_DEPT_ID, REG_DEPT_NM,
#{pubBxDeptId, jdbcType=VARCHAR}, #{apvNo, jdbcType=BIGINT}, GETDATE(),NULL, CONVERT(DATETIME, #{pubBxDt, jdbcType=VARCHAR}, 120), GETDATE(),NULL, CONVERT(DATETIME, #{pubBxEndYmd, jdbcType=VARCHAR}, 120), #{regrUid, jdbcType=VARCHAR}, #{regrNm, jdbcType=NVARCHAR}, #{regDeptId, jdbcType=VARCHAR}, #{regDeptNm, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.updateApOngdPubBxD */ UPDATE AP_${storage}_PUB_BX_D
PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}, APV_NO = #{apvNo, jdbcType=BIGINT},
PUB_BX_DT = GETDATE(), PUB_BX_DT = NULL, PUB_BX_DT = CONVERT(DATETIME, #{pubBxDt, jdbcType=VARCHAR}, 120),
PUB_BX_END_YMD = GETDATE(), PUB_BX_END_YMD = NULL, PUB_BX_END_YMD = CONVERT(DATETIME, #{pubBxEndYmd, jdbcType=VARCHAR}, 120),
REGR_UID = #{regrUid, jdbcType=VARCHAR}, REGR_NM = #{regrNm, jdbcType=NVARCHAR}, REG_DEPT_ID = #{regDeptId, jdbcType=VARCHAR}, REG_DEPT_NM = #{regDeptNm, jdbcType=NVARCHAR},
AND PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR} AND APV_NO = #{apvNo, jdbcType=BIGINT} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdPubBxDDao.deleteApOngdPubBxD */
DELETE FROM AP_${storage}_PUB_BX_D

	AND PUB_BX_DEPT_ID = #{pubBxDeptId, jdbcType=VARCHAR}
	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	${whereSqllet}
