mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.selectApOngdRefDocL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.APV_NO DESC, N.REF_DOC_HST_NO DESC, N.REF_APV_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.APV_NO,
	T.REF_DOC_HST_NO,
	T.REF_APV_NO,
	T.SORT_ORDR,
	T.MODR_UID,
	CONVERT(VARCHAR(19), T.MOD_DT, 120) MOD_DT,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR},'ko')) MODR_NM,

FROM AP_${storage}_REF_DOC_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND T.REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.APV_NO, T.REF_DOC_HST_NO, T.REF_APV_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.countApOngdRefDocL */
SELECT COUNT(*) CNT
FROM AP_${storage}_REF_DOC_L T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND T.REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.insertApOngdRefDocL */ INSERT INTO AP_${storage}_REF_DOC_L
APV_NO, REF_DOC_HST_NO, REF_APV_NO, SORT_ORDR, MODR_UID, MOD_DT,
#{apvNo, jdbcType=BIGINT}, #{refDocHstNo, jdbcType=INTEGER}, #{refApvNo, jdbcType=BIGINT}, #{sortOrdr, jdbcType=INTEGER}, (SELECT ISNULL(MAX(SORT_ORDR),0)+1 FROM AP_${storage}_REF_DOC_L WHERE APV_NO = #{apvNo, jdbcType=VARCHAR} AND REF_DOC_HST_NO = #{refDocHstNo, jdbcType=VARCHAR}), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.updateApOngdRefDocL */
UPDATE AP_${storage}_REF_DOC_L

	APV_NO = #{apvNo, jdbcType=BIGINT},
	REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER},
	REF_APV_NO = #{refApvNo, jdbcType=BIGINT},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		MOD_DT = GETDATE(),
		MOD_DT = NULL,
		MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
	


	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdRefDocLDao.deleteApOngdRefDocL */
DELETE FROM AP_${storage}_REF_DOC_L

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
	AND REF_DOC_HST_NO = #{refDocHstNo, jdbcType=INTEGER}
	AND REF_APV_NO = #{refApvNo, jdbcType=BIGINT}
	${whereSqllet}
