mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdTrxDDao.selectApOngdTrxD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.TRX_APV_NO DESC) AS RNUM, N.* FROM (SELECT
	
	T.TRX_APV_NO,
	T.ORGN_APV_NO,
	T.FORM_ID,
	T.FORM_SEQ,
	T.BODY_HGHT_PX,
	T.FORM_WDTH_TYP_CD,
	T.FOOTER_VA,

FROM AP_${storage}_TRX_D T

	AND T.TRX_APV_NO = #{trxApvNo, jdbcType=BIGINT}
	AND T.ORGN_APV_NO = #{orgnApvNo, jdbcType=BIGINT}
	${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.TRX_APV_NO


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngdTrxDDao.countApOngdTrxD */
SELECT COUNT(*) CNT
FROM AP_${storage}_TRX_D T

	AND T.TRX_APV_NO = #{trxApvNo, jdbcType=BIGINT}
	AND T.ORGN_APV_NO = #{orgnApvNo, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdTrxDDao.insertApOngdTrxD */ INSERT INTO AP_${storage}_TRX_D
TRX_APV_NO, ORGN_APV_NO, FORM_ID, FORM_SEQ, BODY_HGHT_PX, FORM_WDTH_TYP_CD, FOOTER_VA,
#{trxApvNo, jdbcType=BIGINT}, #{orgnApvNo, jdbcType=BIGINT}, #{formId, jdbcType=VARCHAR}, #{formSeq, jdbcType=INTEGER}, NULL#{bodyHghtPx, jdbcType=INTEGER}, #{formWdthTypCd, jdbcType=VARCHAR}, #{footerVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdTrxDDao.updateApOngdTrxD */ UPDATE AP_${storage}_TRX_D
TRX_APV_NO = #{trxApvNo, jdbcType=BIGINT}, ORGN_APV_NO = #{orgnApvNo, jdbcType=BIGINT}, FORM_ID = #{formId, jdbcType=VARCHAR}, FORM_SEQ = #{formSeq, jdbcType=INTEGER}, BODY_HGHT_PX = NULL#{bodyHghtPx, jdbcType=INTEGER}, FORM_WDTH_TYP_CD = #{formWdthTypCd, jdbcType=VARCHAR}, FOOTER_VA = #{footerVa, jdbcType=NVARCHAR},
AND TRX_APV_NO = #{trxApvNo, jdbcType=BIGINT} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdTrxDDao.deleteApOngdTrxD */
DELETE FROM AP_${storage}_TRX_D

	AND TRX_APV_NO = #{trxApvNo, jdbcType=BIGINT}
	${whereSqllet}
