mapper.select:
/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.selectBaBullPopupD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}BRD_ID DESC) AS RNUM,
	
	BULL_ID,
	BRD_ID,
	START_DT,
	END_DT,
	WIDTH,
	DISP_YN,
	USE_YN,
	COMP_ID,
	MODR_UID,
	CONVERT(VARCHAR(19), MOD_DT, 120) MOD_DT,

FROM BA_BULL_POPUP_D

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND CONVERT(VARCHAR(10), START_DT, 120) >= #{todayYmd, jdbcType=VARCHAR}
			 AND CONVERT(VARCHAR(10), END_DT, 120) <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

ORDER BY ${orderBy}
ORDER BY BRD_ID DESC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaBullPopupDDao.countBaBullPopupD */
SELECT COUNT(*) CNT
FROM BA_BULL_POPUP_D 

	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND BULL_ID = #{bullId, jdbcType=VARCHAR}
	 
		
			 AND CONVERT(VARCHAR(10), START_DT, 120) >= #{todayYmd, jdbcType=VARCHAR}
			 AND CONVERT(VARCHAR(10), END_DT, 120) <= #{todayYmd, jdbcType=VARCHAR}
		 
	 
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.insertbaBullPopupD */ INSERT INTO BA_BULL_POPUP_D
BULL_ID, BRD_ID, START_DT, END_DT, WIDTH, DISP_YN, USE_YN, COMP_ID, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120), CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120), #{width, jdbcType=INTEGER}, #{dispYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),

mapper.update:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.updatebaBullPopupD */ UPDATE BA_BULL_POPUP_D
START_DT = CONVERT(DATETIME, #{strtDt, jdbcType=VARCHAR}, 120), END_DT = CONVERT(DATETIME, #{endDt, jdbcType=VARCHAR}, 120), WIDTH = #{width, jdbcType=INTEGER}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, DISP_YN = #{dispYn, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
AND BULL_ID = #{bullId, jdbcType=INTEGER} AND BRD_ID = #{brdId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.baBullPopupDDao.deletebaBullPopupD */
DELETE FROM BA_BULL_POPUP_D

	AND BULL_ID = #{bullId, jdbcType=INTEGER}
	AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=VARCHAR}
