mapper.select:
/* com.innobiz.orange.web.bb.dao.BaCmtDDao.selectBaCmtD */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.CMT_ID DESC) AS RNUM,
	
	T.CMT_ID,
	T.BULL_ID,
	T.CMT,
	T.REGR_UID,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM,
	T.REG_DT,

FROM BA_CMT_D T, BA_RESC_B R

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.CMT_ID = #{cmtId, jdbcType=INTEGER}
	 AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.CMT_ID DESC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaCmtDDao.countBaCmtD */
SELECT COUNT(*) CNT
FROM BA_CMT_D T, BA_RESC_B R

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.CMT_ID = #{cmtId, jdbcType=INTEGER}
	 AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaCmtDDao.insertBaCmtD */ INSERT INTO BA_CMT_D
CMT_ID, BULL_ID, CMT, REGR_UID, REG_DT,
#{cmtId, jdbcType=VARCHAR}, #{bullId, jdbcType=VARCHAR}, #{cmt, jdbcType=NVARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120),

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaCmtDDao.deleteBaCmtD */
DELETE FROM BA_CMT_D

	AND CMT_ID = #{cmtId, jdbcType=INTEGER}
