mapper.select:
/* com.innobiz.orange.web.bb.dao.BaReadHstLDao.selectBaReadHstL */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}T.READ_DT DESC) AS RNUM,
	
	T.BULL_ID,
	T.USER_UID,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	T.READ_DT,

FROM BA_READ_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.READ_DT DESC

) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaReadHstLDao.countBaReadHstL */
SELECT COUNT(*) CNT
FROM BA_READ_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaReadHstLDao.insertBaReadHstL */ INSERT INTO BA_READ_HST_L
BULL_ID, USER_UID, READ_DT,
#{bullId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, GETDATE(),

mapper.update:
/* com.innobiz.orange.web.bb.dao.BaReadHstLDao.updateBaReadHstL */
UPDATE BA_READ_HST_L

	READ_DT = GETDATE(),


	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaReadHstLDao.deleteBaReadHstL */
DELETE FROM BA_READ_HST_L

	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
