mapper.select:
/* com.innobiz.orange.web.ct.dao.CtPrBDao.selectCtPrB */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.BULL_ID DESC) AS RNUM, N.* FROM ( SELECT
CPB_T.BULL_ID, CPB_T.COMP_ID, CPB_T.CT_ID, CPB_T.CT_SUBJ_RESC_ID, CPB_T.CT_ITRO, CPB_T.BULL_PID, CPB_T.REPLY_GRP_ID, CPB_T.REPLY_ORDR, CPB_T.REPLY_DPTH, CPB_T.BULL_STAT_CD, CPB_T.BULL_REZV_YN, CPB_T.TGT_DEPT_YN, CPB_T.TGT_USER_YN, CPB_T.SUBJ, CPB_T.BULL_REZV_DT, CPB_T.BULL_EXPR_DT, CPB_T.CONT, CPB_T.READ_CNT, CPB_T.PROS_CNT, CPB_T.CONS_CNT, CPB_T.RECMD_CNT, CPB_T.REGR_UID, CPB_T.REG_DT, CPB_T.MODR_UID, CPB_T.MOD_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CPB_T.REGR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CPB_T.MODR_UID) AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, ( SELECT CASE WHEN COUNT(1) > 0 THEN 'Y' ELSE 'N' END ATT_YN FROM CT_FILE_D WHERE REF_ID=CONVERT(VARCHAR, CPB_T.BULL_ID)
) AS ATT_YN,
FROM CT_PR_B CPB_T
LEFT JOIN OR_USER_B U ON CPB_T.REGR_UID = U.USER_UID LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = ISNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY CPB_T.BULL_ID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtPrBDao.insertCtPrB */ INSERT INTO CT_PR_B
BULL_ID, COMP_ID, CT_ID, CT_SUBJ_RESC_ID, CT_ITRO, BULL_PID, REPLY_GRP_ID, REPLY_ORDR, REPLY_DPTH, BULL_STAT_CD, BULL_REZV_YN, TGT_DEPT_YN, TGT_USER_YN, SUBJ, BULL_REZV_DT, BULL_EXPR_DT, CONT, READ_CNT, PROS_CNT, CONS_CNT, RECMD_CNT, REGR_UID, REG_DT, MODR_UID, MOD_DT, ATT_YN,
#{bullId, jdbcType=NUMERIC}, #{compId, jdbcType=VARCHAR}, #{ctId, jdbcType=VARCHAR}, #{ctSubjRescId, jdbcType=VARCHAR}, #{ctItro, jdbcType=VARCHAR}, #{bullPid, jdbcType=NUMERIC}, #{replyGrpId, jdbcType=NUMERIC}, #{replyOrdr, jdbcType=NUMERIC}, #{replyDpth, jdbcType=NUMERIC}, #{bullStatCd, jdbcType=VARCHAR}, #{bullRezvYn, jdbcType=CHAR}, #{tgtDeptYn, jdbcType=CHAR}, #{tgtUserYn, jdbcType=CHAR}, #{subj, jdbcType=NVARCHAR}, NULL, CONVERT(DATETIME, #{bullRezvDt, jdbcType=VARCHAR}, 120), NULL, CONVERT(DATETIME, #{bullExprDt, jdbcType=VARCHAR}, 120), #{cont, jdbcType=NVARCHAR}, #{readCnt, jdbcType=NUMERIC}, #{prosCnt, jdbcType=NUMERIC}, #{consCnt, jdbcType=NUMERIC}, #{recmdCnt, jdbcType=NUMERIC}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, GETDATE(),GETDATE(),NULL, CONVERT(DATETIME, #{regDt, jdbcType=VARCHAR}, 120), #{modrUid, jdbcType=VARCHAR}, GETDATE(),NULL, CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120), #{attYn, jdbcType=CHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtPrBDao.updateCtPrB */ UPDATE CT_PR_B
CT_ID = #{ctId, jdbcType=VARCHAR}, CT_ID = #{ctSubjRescId, jdbcType=VARCHAR}, CT_ITRO = #{ctItro, jdbcType=VARCHAR}, BULL_PID = #{bullPid, jdbcType=NUMERIC}, REPLY_GRP_ID = #{replyGrpId, jdbcType=NUMERIC}, REPLY_ORDR = #{replyOrdr, jdbcType=NUMERIC}, REPLY_DPTH = #{replyDpth, jdbcType=NUMERIC}, BULL_STAT_CD = #{bullStatCd, jdbcType=VARCHAR}, BULL_REZV_YN = #{bullRezvYn, jdbcType=CHAR}, TGT_DEPT_YN = #{tgtDeptYn, jdbcType=CHAR}, TGT_USER_YN = #{tgtUserYn, jdbcType=CHAR}, SUBJ = #{subj, jdbcType=NVARCHAR}, BULL_REZV_DT = CONVERT(DATETIME, #{bullRezvDt, jdbcType=VARCHAR}, 120), BULL_EXPR_DT = CONVERT(DATETIME, #{bullExprDt, jdbcType=VARCHAR}, 120), CONT = #{cont, jdbcType=NVARCHAR}, READ_CNT = #{readCnt, jdbcType=NUMERIC}, PROS_CNT = #{prosCnt, jdbcType=NUMERIC}, CONS_CNT = #{consCnt, jdbcType=NUMERIC}, RECMD_CNT = #{recmdCnt, jdbcType=NUMERIC}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = GETDATE(), MOD_DT = NULL, MOD_DT = CONVERT(DATETIME, #{modDt, jdbcType=VARCHAR}, 120),
ATT_YN = #{attYn, jdbcType=CHAR},
AND BULL_ID = #{bullId, jdbcType=NUMERIC} AND COMP_ID = #{compId, jdbcType=VARCHAR}

/* com.innobiz.orange.web.ct.dao.CtPrBDao.updateReadCnt */
UPDATE CT_PR_B

	READ_CNT = READ_CNT + 1,


	AND BULL_ID = #{bullId, jdbcType=NUMERIC}

mapper.delete:
	/* com.innobiz.orange.web.ct.dao.CtPrBDao.deleteCtPrB */
	DELETE FROM CT_PR_B
	
	     AND BULL_ID = #{bullId, jdbcType=NUMERIC}
	     AND COMP_ID = #{compId, jdbcType=VARCHAR}
