mapper.select:
/* com.innobiz.orange.web.ct.dao.CtRescBDao.selectCtRescB */

SELECT * FROM (

SELECT

	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.RESC_ID DESC, N.LANG_TYP_CD DESC) AS RNUM, N.* FROM (SELECT
	
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA,

FROM CT_RESC_B T

	 AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND T.RESC_VA = #{rescVa, jdbcType=VARCHAR}
	 AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	
	 ${whereSqllet}


	ORDER BY ${orderBy}
	ORDER BY T.RESC_ID, T.LANG_TYP_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtRescBDao.countCtRescB */
SELECT COUNT(*) CNT
FROM CT_RESC_B T
 
	INNER JOIN CT_ESTB_B CTE ON T.RESC_ID = CTE.CT_SUBJ_RESC   


    		    
	 AND T.RESC_ID NOT IN (#{rescId, jdbcType=VARCHAR})
	 AND T.RESC_VA = #{rescVa, jdbcType=VARCHAR}
	 AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtRescBDao.insertCtRescB */ INSERT INTO CT_RESC_B
RESC_ID, LANG_TYP_CD, RESC_VA,
#{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtRescBDao.updateCtRescB */
UPDATE CT_RESC_B

	 RESC_ID = #{rescId, jdbcType=VARCHAR},
	 LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	 RESC_VA = #{rescVa, jdbcType=NVARCHAR},


	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtRescBDao.deleteCtRescB */
DELETE FROM CT_RESC_B

	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	 ${whereSqllet}
