mapper.select:
/* com.innobiz.orange.web.ct.dao.CtSeculCdBDao.selectCtSeculCdB */


SELECT * FROM (

SELECT


	
	TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_SECUL_CD DESC) AS RNUM, N.* 
	FROM (
		SELECT
	
	CSCB_T.COMP_ID,
	CSCB_T.USER_SECUL_CD,
	CSCB_T.USER_SECUL_CD_SUBJ_RESC_ID,
	

FROM CT_SECUL_CD_B CSCB_T


	ORDER BY ${orderBy}
	ORDER BY CSCB_T.USER_SECUL_CD


) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtSeculCdBDao.countCtSeculCdB */
SELECT COUNT(*) CNT
FROM CT_SECUL_CD_B CSCB_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtSeculCdBDao.insertCtSeculCdB */ INSERT INTO CT_SECUL_CD_B
COMP_ID, USER_SECUL_CD, USER_SECUL_CD_SUBJ_RESC_ID,
#{compId, jdbcType=CHAR}, #{userSeculCd, jdbcType=VARCHAR}, #{userSeculCdSubjRescId, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtSeculCdBDao.updateCtSeculCdB */
UPDATE CT_SECUL_CD_B

	 USER_SECUL_CD_SUBJ_RESC_ID = #{userSeculCdSubjRescId, jdbcType=VARCHAR},


     AND CSCB_T.COMP_ID = #{compId, jdbcType=CHAR}
     AND CSCB_T.USER_SECUL_CD = #{userSeculCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtSeculCdBDao.deleteCtSeculCdB */
DELETE FROM CT_SECUL_CD_B

     AND CSCB_T.COMP_ID = #{compId, jdbcType=CHAR}
     AND CSCB_T.USER_SECUL_CD = #{userSeculCd, jdbcType=VARCHAR}
