mapper.select:
/* com.innobiz.orange.web.ct.dao.CtVistrHstDDao.selectCtVistrHstCountSumD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC) AS RNUM, N.* FROM ( SELECT
CMD.COMP_ID ,CMD.CT_ID ,CMD.USER_UID ,CMD.USER_SECUL_CD ,CMD.JOIN_DT ,CVHD_T.ACCS_TYP_CD ,( SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = ( SELECT RESC_ID FROM OR_USER_B OU_USER_T WHERE OU_USER_T.USER_UID = CMD.USER_UID ) AND LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR} )USER_NM ,( SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = ( SELECT DEPT_RESC_ID FROM OR_USER_B OU_DEPT_T WHERE OU_DEPT_T.USER_UID = CMD.USER_UID ) AND LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR} )DEPT_NM ,( COUNT(CASE WHEN CONVERT(VARCHAR(10), CVHD_T.ACCS_DT, 120) BETWEEN #{strtDt, jdbcType=VARCHAR} AND #{endDt, jdbcType=VARCHAR} THEN 'Y' END )) CT_TODAY_COUNT ,( COUNT(CMD.USER_UID) ) CT_ALL_COUNT
FROM CT_VISTR_HST_D CVHD_T INNER JOIN CT_MBSH_D CMD ON CVHD_T.CT_ID=CMD.CT_ID AND CMD.USER_UID = CVHD_T.USER_UID
GROUP BY CMD.USER_UID, CMD.USER_SECUL_CD, CMD.JOIN_DT, CMD.USER_SECUL_CD, CMD.COMP_ID ,CMD.CT_ID, CVHD_T.ACCS_TYP_CD, CVHD_T.USER_UID
ORDER BY ${orderBy} ORDER BY CVHD_T.USER_UID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtVistrHstDDao.selectCtVistrHstD */
SELECT * FROM (
SELECT
TOP (#{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) ROW_NUMBER() OVER (ORDER BY ${orderBy}N.USER_UID DESC) AS RNUM, N.* FROM ( SELECT
CVHD_T.COMP_ID, CVHD_T.CT_ID, CVHD_T.USER_UID, CVHD_T.ACCS_DT,
CVHD_T.ACCS_TYP_CD,
FROM CT_VISTR_HST_D CVHD_T
ORDER BY ${orderBy} ORDER BY CVHD_T.USER_UID
) N ) M WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} AND RNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtVistrHstDDao.countCtVistrHstD */
SELECT COUNT(*) CNT
FROM CT_VISTR_HST_D CVHD_T

